import React, { useState } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faBars } from "@fortawesome/free-solid-svg-icons";

function Navigation(){
    const[showMenu, setShowMenu] = useState(false)

    let menu;

    if (showMenu) {
        menu = (
        <div className="fixed bg-white top-0 left-0 w-64 p-4 shadow">
            {/* Additional styling classes and content for the menu */}
            <p>Menu Item 1</p>
            <p>Menu Item 2</p>
            <p>Menu Item 3</p>
        </div>
        );
    }

    return(
        <nav className="p-4 bg-gray-700 text-white">
            <span className="text-xl cursor-pointer" onClick={() => setShowMenu(!showMenu)}>
                <FontAwesomeIcon icon={faBars} />
            </span>
            
            {menu}
        </nav>
    )
}

export default Navigation
